MINIMALDARK.css:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

:root {
    --bg: #0b0d10;
    --panel: #0f1115;
    --muted: #bfc8cf;
    --text: #e6eef6;
    --heading: #ffffff;
    --border: #22262a;
    --accent: #58a6ff;
    --accent-strong: #1f6feb;
    --danger: #ffb86b;
    --code-bg: #011627;
    --code-fg: #ffd8a6;
}

body {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', sans-serif;
    background-color: var(--bg);
    color: var(--text);
    line-height: 1.8;
    padding: 40px 20px;
    max-width: 800px;
    margin: 0 auto;
}

h1, h2, h3, h4, h5, h6 {
    color: var(--heading);
    margin: 30px 0 15px 0;
    font-weight: 600;
    letter-spacing: -0.5px;
}

h1 {
    font-size: 2.5em;
    border-bottom: 3px solid var(--border);
    padding-bottom: 10px;
}

h2 {
    font-size: 2em;
    border-bottom: 2px solid rgba(255,255,255,0.04);
    padding-bottom: 8px;
}

h3 {
    font-size: 1.5em;
}

h4 {
    font-size: 1.25em;
}

h5 {
    font-size: 1.1em;
}

h6 {
    font-size: 1em;
}

p {
    margin: 15px 0;
    color: var(--muted);
}

a {
    color: var(--accent);
    text-decoration: none;
    border-bottom: 1px solid transparent;
    transition: border-bottom 0.2s ease, color 0.15s ease;
}

a:hover {
    border-bottom: 1px solid var(--accent);
    color: var(--accent-strong);
}

button {
    background-color: var(--accent-strong);
    color: #ffffff;
    border: none;
    padding: 12px 24px;
    font-size: 0.95em;
    font-family: inherit;
    cursor: pointer;
    border-radius: 4px;
    transition: background-color 0.2s ease;
    margin: 5px;
    box-shadow: 0 2px 6px rgba(15,17,21,0.6);
}

button:hover {
    background-color: #2a79ff;
}

div {
    margin: 20px 0;
    padding: 20px;
    background-color: var(--panel);
    border-radius: 4px;
    border: 1px solid var(--border);
}

.highlighted {
    background-color: #17191c;
    border-left: 4px solid var(--danger);
    color: var(--text);
}

input, textarea {
    background-color: #0b0d10;
    border: 2px solid var(--border);
    color: var(--text);
    padding: 10px 12px;
    font-family: inherit;
    font-size: 1em;
    border-radius: 4px;
    margin: 5px 0;
    width: 100%;
    max-width: 400px;
    transition: border-color 0.2s ease, box-shadow 0.12s ease;
}

input::placeholder, textarea::placeholder {
