MINIMAL.css:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', sans-serif;
    background-color: #ffffff;
    color: #333333;
    line-height: 1.8;
    padding: 40px 20px;
    max-width: 800px;
    margin: 0 auto;
}

h1, h2, h3, h4, h5, h6 {
    color: #111111;
    margin: 30px 0 15px 0;
    font-weight: 600;
    letter-spacing: -0.5px;
}

h1 {
    font-size: 2.5em;
    border-bottom: 3px solid #111111;
    padding-bottom: 10px;
}

h2 {
    font-size: 2em;
    border-bottom: 2px solid #e0e0e0;
    padding-bottom: 8px;
}

h3 {
    font-size: 1.5em;
}

h4 {
    font-size: 1.25em;
}

h5 {
    font-size: 1.1em;
}

h6 {
    font-size: 1em;
}

p {
    margin: 15px 0;
    color: #555555;
}

a {
    color: #0066cc;
    text-decoration: none;
    border-bottom: 1px solid transparent;
    transition: border-bottom 0.2s ease;
}

a:hover {
    border-bottom: 1px solid #0066cc;
}

button {
    background-color: #111111;
    color: #ffffff;
    border: none;
    padding: 12px 24px;
    font-size: 0.95em;
    font-family: inherit;
    cursor: pointer;
    border-radius: 4px;
    transition: background-color 0.2s ease;
    margin: 5px;
}

button:hover {
    background-color: #333333;
}

div {
    margin: 20px 0;
    padding: 20px;
    background-color: #fafafa;
    border-radius: 4px;
}

.highlighted {
    background-color: #fff9e6;
    border-left: 4px solid #ffcc00;
}

input, textarea {
    background-color: #ffffff;
    border: 2px solid #e0e0e0;
    color: #333333;
    padding: 10px 12px;
    font-family: inherit;
    font-size: 1em;
    border-radius: 4px;
    margin: 5px 0;
    width: 100%;
    max-width: 400px;
    transition: border-color 0.2s ease;
}

input:focus, textarea:focus {
    outline: none;
    border-color: #111111;
}

textarea {
    resize: vertical;
    min-height: 100px;
}

ul, ol {
    margin: 15px 0 15px 30px;
    color: #555555;
}

li {
    margin: 10px 0;
}

blockquote {
    border-left: 4px solid #e0e0e0;
    padding-left: 20px;
    margin: 20px 0;
    font-style: italic;
    color: #777777;
    background-color: #fafafa;
    padding: 15px 20px;
    border-radius: 4px;
}

code {
    background-color: #f5f5f5;
    color: #d73a49;
    padding: 3px 8px;
    border-radius: 3px;
    font-family: 'Courier New', Courier, monospace;
    font-size: 0.9em;
}

strong {
    color: #111111;
    font-weight: 700;
}

em {
    color: #555555;
    font-style: italic;
}

hr {
    border: none;
    height: 1px;
    background-color: #e0e0e0;
    margin: 40px 0;
}

img {
    max-width: 100%;
    height: auto;
    border-radius: 4px;
    margin: 15px 0;
    display: block;
}

/* Container and wrapper styles */
.container, .wrapper {
    background-color: #f5f5f5;
    border: 1px solid #e0e0e0;
    border-radius: 4px;
    padding: 25px;
    margin: 25px 0;
}

.inner-section {
    background-color: #ffffff;
    border-left: 3px solid #cccccc;
}

.highlight {
    background-color: #ffffff;
    border: 2px solid #111111;
}

CODIX.css:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Consolas', 'Monaco', 'Courier New', monospace;
    background-color: #0d1117;
    color: #00ff41;
    line-height: 1.6;
    padding: 20px;
    max-width: 1200px;
    margin: 0 auto;
}

h1, h2, h3, h4, h5, h6 {
    color: #00ff41;
    margin: 20px 0 10px 0;
    font-weight: 700;
    text-shadow: 0 0 10px rgba(0, 255, 65, 0.3);
}

h1 {
    font-size: 2.5em;
    border-bottom: 2px solid #00ff41;
    padding-bottom: 10px;
}

h2 {
    font-size: 2em;
    border-bottom: 1px solid #1f6feb;
}

h3 {
    font-size: 1.5em;
    color: #1f6feb;
}

p {
    margin: 15px 0;
    color: #c9d1d9;
}

a {
    color: #58a6ff;
    text-decoration: none;
    transition: all 0.3s ease;
}

a:hover {
    color: #00ff41;
    text-shadow: 0 0 5px rgba(0, 255, 65, 0.5);
}

button {
    background-color: #238636;
    color: #ffffff;
    border: 2px solid #00ff41;
    padding: 10px 20px;
    font-size: 1em;
    font-family: 'Consolas', monospace;
    cursor: pointer;
    border-radius: 6px;
    transition: all 0.3s ease;
    margin: 5px;
}

button:hover {
    background-color: #2ea043;
    box-shadow: 0 0 15px rgba(0, 255, 65, 0.4);
    transform: translateY(-2px);
}

div {
    margin: 15px 0;
    padding: 15px;
    background-color: #161b22;
    border-radius: 6px;
    border-left: 4px solid #00ff41;
}

.highlighted {
    background-color: #1f6feb22;
    border-left: 4px solid #1f6feb;
    box-shadow: 0 0 10px rgba(31, 111, 235, 0.2);
}

input, textarea {
    background-color: #0d1117;
    border: 2px solid #30363d;
    color: #c9d1d9;
    padding: 10px;
    font-family: 'Consolas', monospace;
    font-size: 1em;
    border-radius: 6px;
    margin: 5px 0;
    width: 100%;
    max-width: 400px;
}

input:focus, textarea:focus {
    outline: none;
    border-color: #00ff41;
    box-shadow: 0 0 5px rgba(0, 255, 65, 0.3);
}

textarea {
    resize: vertical;
    min-height: 100px;
}

ul, ol {
    margin: 15px 0 15px 30px;
    color: #c9d1d9;
}

li {
    margin: 8px 0;
}

blockquote {
    border-left: 4px solid #1f6feb;
    padding-left: 20px;
    margin: 20px 0;
    font-style: italic;
    color: #8b949e;
    background-color: #161b22;
    padding: 15px 20px;
    border-radius: 6px;
}

code {
    background-color: #161b22;
    color: #ff7b72;
    padding: 3px 8px;
    border-radius: 4px;
    font-family: 'Consolas', 'Monaco', monospace;
    font-size: 0.9em;
    border: 1px solid #30363d;
}

strong {
    color: #00ff41;
    font-weight: bold;
}

em {
    color: #1f6feb;
    font-style: italic;
}

hr {
    border: none;
    height: 2px;
    background: linear-gradient(to right, #00ff41, #1f6feb, #00ff41);
    margin: 30px 0;
    box-shadow: 0 0 10px rgba(0, 255, 65, 0.3);
}

img {
    max-width: 100%;
    height: auto;
    border-radius: 8px;
    border: 2px solid #30363d;
    margin: 10px 0;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.5);
}

/* Container and wrapper styles */
.container, .wrapper {
    background-color: #0d1117;
    border: 2px solid #30363d;
    border-radius: 8px;
    padding: 20px;
    margin: 20px 0;
}

.inner-section {
    background-color: #161b22;
    border-left: 3px solid #1f6feb;
}

.highlight {
    background: linear-gradient(135deg, #1f6feb22, #00ff4122);
    border: 2px solid #00ff41;
}

MINIMALDARK.css:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

:root {
    --bg: #0b0d10;
    --panel: #0f1115;
    --muted: #bfc8cf;
    --text: #e6eef6;
    --heading: #ffffff;
    --border: #22262a;
    --accent: #58a6ff;
    --accent-strong: #1f6feb;
    --danger: #ffb86b;
    --code-bg: #011627;
    --code-fg: #ffd8a6;
}

body {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', sans-serif;
    background-color: var(--bg);
    color: var(--text);
    line-height: 1.8;
    padding: 40px 20px;
    max-width: 800px;
    margin: 0 auto;
}

h1, h2, h3, h4, h5, h6 {
    color: var(--heading);
    margin: 30px 0 15px 0;
    font-weight: 600;
    letter-spacing: -0.5px;
}

h1 {
    font-size: 2.5em;
    border-bottom: 3px solid var(--border);
    padding-bottom: 10px;
}

h2 {
    font-size: 2em;
    border-bottom: 2px solid rgba(255,255,255,0.04);
    padding-bottom: 8px;
}

h3 {
    font-size: 1.5em;
}

h4 {
    font-size: 1.25em;
}

h5 {
    font-size: 1.1em;
}

h6 {
    font-size: 1em;
}

p {
    margin: 15px 0;
    color: var(--muted);
}

a {
    color: var(--accent);
    text-decoration: none;
    border-bottom: 1px solid transparent;
    transition: border-bottom 0.2s ease, color 0.15s ease;
}

a:hover {
    border-bottom: 1px solid var(--accent);
    color: var(--accent-strong);
}

button {
    background-color: var(--accent-strong);
    color: #ffffff;
    border: none;
    padding: 12px 24px;
    font-size: 0.95em;
    font-family: inherit;
    cursor: pointer;
    border-radius: 4px;
    transition: background-color 0.2s ease;
    margin: 5px;
    box-shadow: 0 2px 6px rgba(15,17,21,0.6);
}

button:hover {
    background-color: #2a79ff;
}

div {
    margin: 20px 0;
    padding: 20px;
    background-color: var(--panel);
    border-radius: 4px;
    border: 1px solid var(--border);
}

.highlighted {
    background-color: #17191c;
    border-left: 4px solid var(--danger);
    color: var(--text);
}

input, textarea {
    background-color: #0b0d10;
    border: 2px solid var(--border);
    color: var(--text);
    padding: 10px 12px;
    font-family: inherit;
    font-size: 1em;
    border-radius: 4px;
    margin: 5px 0;
    width: 100%;
    max-width: 400px;
    transition: border-color 0.2s ease, box-shadow 0.12s ease;
}

input::placeholder, textarea::placeholder {
    color: rgba(190,200,210,0.5);
}

input:focus, textarea:focus {
    outline: none;
    border-color: var(--accent);
    box-shadow: 0 0 0 3px rgba(88,166,255,0.08);
}

textarea {
    resize: vertical;
    min-height: 100px;
}

ul, ol {
    margin: 15px 0 15px 30px;
    color: var(--muted);
}

li {
    margin: 10px 0;
}

blockquote {
    border-left: 4px solid var(--border);
    padding-left: 20px;
    margin: 20px 0;
    font-style: italic;
    color: #aab3ba;
    background-color: #0f1113;
    padding: 15px 20px;
    border-radius: 4px;
}

code {
    background-color: var(--code-bg);
    color: var(--code-fg);
    padding: 4px 8px;
    border-radius: 3px;
    font-family: 'Courier New', Courier, monospace;
    font-size: 0.9em;
    display: inline-block;
}

pre {
    background-color: var(--code-bg);
    color: var(--code-fg);
    padding: 12px;
    border-radius: 6px;
    overflow: auto;
    border: 1px solid rgba(255,255,255,0.03);
}

strong {
    color: var(--heading);
    font-weight: 700;
}

em {
    color: var(--muted);
    font-style: italic;
}

hr {
    border: none;
    height: 1px;
    background-color: rgba(255,255,255,0.04);
    margin: 40px 0;
}

img {
    max-width: 100%;
    height: auto;
    border-radius: 4px;
    margin: 15px 0;
    display: block;
    border: 1px solid var(--border);
}

/* Container and wrapper styles */
.container, .wrapper {
    background-color: #0d0f12;
    border: 1px solid var(--border);
    border-radius: 4px;
    padding: 25px;
    margin: 25px 0;
}

.inner-section {
    background-color: #0b0d0f;
    border-left: 3px solid rgba(255,255,255,0.03);
}

.highlight {
    background-color: #0e1013;
    border: 2px solid var(--accent-strong);
}
